import React, { useContext, useEffect, useState } from "react";
import { ConvContext } from "../../contexts/ConvContext";
import styled from "styled-components";


const ConvList = ({convIds}) => {

    const { convs } = useContext(ConvContext);
    const getConversations = () => {
        let conversationList = [];

        for(let i=0;i<convIds.length;i++){
            // console.log(convIds[i]);

            if(convs[i].conversationId === convIds[i]){
                conversationList.push(convs[i]);
            }

        }
        console.log(conversationList);
        return conversationList;
    }
    const [conversationList, setConversationList] = useState(getConversations()); 


    // useEffect(()=>{
    //     console.log(convIds);
    // },[]);

    return(
        <StyledArea>
            {conversationList.map(
            <UserItem> 
                <MessageTitle>Title</MessageTitle>
                <MessagePreview>Perview</MessagePreview>
            </UserItem>
            )}
        </StyledArea>
    );
}

export default ConvList;

const StyledArea = styled.div`
    padding: 10px;
`;

const UserItem = styled.div`
    // display: flex;
    // display: grid;
    // grid-template-columns: 6fr 1fr;
    // align-items: center;
    list-style-type: none;
    text-decoration: none;
    background: ${(props) => (props.selected ? "var(--trans-white-60)" : "var(--trans-white-20)")};
    border-radius: 10px;
    transition: all 0.2s;
    padding: 10px;
    cursor: pointer;

    &:not(:last-child) {
        margin-bottom: 20px;
    }
`;

const MessageTitle = styled.div`
    font-weight: bold;
    font-size: var(--h2-size);
`;

const MessagePreview = styled.div`
    font-size: var(--p-size);
`;
