import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import ConvList from "./ConvList";
import Messages from "./Messages";
import ConvContext from "../../contexts/ConvContext"
import MsgContext from "../../contexts/MsgContext"
import { UserContext } from "../../contexts/UserContext";

const Chat = ({userId}) => {
    const user = users.find((user) => user.userId === userId);
    const { users } = useContext(UserContext);
    const [ conversationIds, setConversationIds ] = useState([user.convIds]);

    useEffect(() => {
        console.log("---------");
        console.log("userId:", userId);
        let user = users.find((user) => user.userId === userId);
        console.log(user);
        console.log(user.convIds);
        // setConversationIds(user.convIds);
        // console.log("user:", user);
        // console.log("convs:", conversationIds);
    },[conversationIds])

    return(
        <ConvContext>
            <MsgContext>
                <StyledArea>
                    <LeftArea>
                        <ConvList convIds={conversationIds}/>
                    </LeftArea>
                    <RightArea>
                        <Messages/>
                    </RightArea>
                </StyledArea>
            </MsgContext>
        </ConvContext>
    );
}

export default Chat;

const StyledArea = styled.div`
    // overflow: auto;
    // height: 100vh;
    display: grid;
    grid-template-columns: 1fr 2fr;
    // border-bottom: 2px solid var(--dark-grey);
`;

const LeftArea = styled.div`
    background: var(--trans-red);
`;
const RightArea = styled.div`
    background: var(--trans-blue);
`;
