import React from "react";
import styled from "styled-components";
import Chat from "./Chat/Chat";

const MainContentArea = () => {
    const loggedInUserId = 1;
    return (
        <StyledArea>
            <Overlay/>
            <Popup>
                {/* <PopupTitle>Cretae User Now</PopupTitle>
                <PopupDescription>Do it!</PopupDescription> */}
                <NewQualificationWrapper>


                <StyledInput
                    type="text"
                    placeholder="Username..."
                />
                <StyledButton>Create. User.</StyledButton>
                </NewQualificationWrapper>
            </Popup>
            <Chat userId = {loggedInUserId}/>
        </StyledArea>
    );
}

export default MainContentArea;

const Overlay = styled.div`
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: rgba(0, 0, 0, 0.5);
    z-index: 2;
`;

const Popup = styled.div`
// display: flex;
    align-items: center;
    justify-content: center;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding: 20px;
    background: var(--dark-grey);
    border-radius: 10px;
    float: left;
    box-sizing: border-box;
    border: 2px solid white;
    transition: all 0.2s;
    z-index: 3;
`;

const PopupTitle = styled.div`
    font-size: var(--h2-size);
    font-weight: 600;
    padding: 0px 0px;
    text-align: center;
`;

const PopupDescription = styled.div`
    font-size: 16px;
    text-align: center;
    color: var(--light-grey);
    padding: 20px;
`;

const StyledButton = styled.button`
    display: flex;
    text-decoration: none;
    justify-content: center;
    padding: 10px 20px;
    background: var(--light-grey);
    color: var(--dark-grey);
    font-weight: 600;
    font-size: 1rem;
    border: 0;
    border-radius: 6px;
    // margin-top: 10px;
    // margin-right: 10px;
    cursor: pointer;

    &:focus {
        outline: none;
    }
`;

const NewQualificationWrapper = styled.div`
    display: flex;
`;

const StyledInput = styled.input`
    border: 0;
    border-radius: 6px;
    display: block;
    width: 100%;
    padding: 20px;
    // font-size: 15px;
    font-size: 1rem;
    margin-right: 4px;
    color: var(--black);
    background: var(--light-grey);
`;

const StyledArea = styled.div`
    display: grid;
    // grid-template-columns: 1fr 1fr;
    padding: 20px;
    background: var(--app-background);
    overflow: auto;
    height: 100vh;
`;
