import React, { createContext, useState } from "react";
import axios from "axios";


export const UserContext = createContext();

const UserContextProvider = props => {

    const [users, setUsers] = useState([
        {
            customId: 0,
            name: "No one is online...",
            convIds: []
        },
        {
            customId: 1,
            name: "User A",
            convIds: [1,2]
        },
        {
            customId: 2,
            name: "User B",
            convIds: [1]
        },
        {
            customId: 3,
            name: "User C",
            convIds: [1]
        }
    ]);

    const addUserToDB = async (newUsr) => {
        await axios.post('http://localhost:5000/users/add', newUsr)
        .then(res => {
            console.log(res.data)
        })
        .catch(err => {
            console.log("error: "+err);
            if(newUsr.username.length < 3){
                setUsernameIsTooShort(true);
            }
        });
    }

    return(
        <UserContext.Provider value={{users, addUserToDB}}>
            {props.children}
        </UserContext.Provider>
    )
}

export default UserContextProvider;
