import React, { useContext, useEffect } from "react";
import { ConvContext } from "../../contexts/ConvContext";
import styled from "styled-components";


const ConvList = ({convIds}) => {

    const { convs } = useContext(ConvContext);
    const conversations = () => {
        let conversationList = [];

        for(let i=0;i<convIds.length;i++){
            // console.log(convIds[i]);

            if(convs[i].conversationId === convIds[i]){
                conversationList.push(convs[i]);
            }

        }
        console.log(conversationList);
        return conversationList;
    }

    // useEffect(()=>{
    //     console.log(convIds);
    // },[]);

    return(
        <UserItem></UserItem>
    );
}

export default ConvList;

const UserItem = styled.div`
    // display: flex;
    display: grid;
    grid-template-columns: 6fr 1fr;
    align-items: center;
    list-style-type: none;
    text-decoration: none;
    background: ${(props) => (props.selected ? "white" : "")};
    border-radius: 10px;
    transition: all 0.2s;
    padding: 10px;
    width: 95%;
    cursor: pointer;

    &:not(:last-child) {
        margin-bottom: 20px;
    }
`;
